        {
          searchResults = []
          if (userText[i].toUpperCase().indexOf(filter) > -1) 
          {
            div = document.getElementById("searchUsers" + i)
            searchResults.push(div)
            div.style.display = ""
          } 
          else 
          {
            div = document.getElementById("searchUsers" + i);
            div.style.display = "none";
          }
        }

    // prints either LoginInput or PwdInput
}

  render() { 
